"use strict";$(document).ready(function(){setTimeout(function(){var i=$(".vertical-timeline-block");i.each(function(){$(this).offset().top>$(window).scrollTop()+.75*$(window).height()&&$(this).find(".vertical-timeline-img, .vertical-timeline-content").addClass("is-hidden")}),$(window).on("scroll",function(){i.each(function(){$(this).offset().top<=$(window).scrollTop()+.75*$(window).height()&&$(this).find(".vertical-timeline-img").hasClass("is-hidden")&&$(this).find(".vertical-timeline-img, .vertical-timeline-content").removeClass("is-hidden").addClass("bounce-in")})})},1500)});